@app.callback(
    [Output('toast-container', 'children', allow_duplicate=True),
     Output('profile-edit-modal', 'is_open', allow_duplicate=True)],
    Input('profile-change-password-btn', 'n_clicks'),
    [State('profile-current-password', 'value'),
     State('profile-new-password', 'value'),
     State('profile-new-password-confirm', 'value')],
    prevent_initial_call=True
)
def change_password_from_profile(n_clicks, current_password, new_password, confirm_password):
    """Change password from profile edit modal"""
    if n_clicks is None:
        raise dash.exceptions.PreventUpdate

    if not current_user.is_authenticated:
        toast = dbc.Toast(
            "Not authenticated",
            header="Authentication Required",
            icon="danger",
            color="danger",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, dash.no_update

    if not current_password or not new_password or not confirm_password:
        toast = dbc.Toast(
            "Please fill in all password fields",
            header="Validation Error",
            icon="warning",
            color="warning",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, dash.no_update

    if new_password != confirm_password:
        toast = dbc.Toast(
            "New passwords do not match",
            header="Validation Error",
            icon="warning",
            color="warning",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, dash.no_update

    if len(new_password) < 6:
        toast = dbc.Toast(
            "Password must be at least 6 characters",
            header="Validation Error",
            icon="warning",
            color="warning",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, dash.no_update

    # Verify current password
    user = auth_manager.verify_user(current_user.username, current_password)
    if not user:
        toast = dbc.Toast(
            "Current password is incorrect",
            header="Verification Failed",
            icon="danger",
            color="danger",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, dash.no_update

    # Change password
    success = auth_manager.change_password(current_user.id, new_password)

    if success:
        toast = dbc.Toast(
            "Password changed successfully!",
            header="Password Updated",
            icon="success",
            color="success",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, False
    else:
        toast = dbc.Toast(
            "Failed to change password",
            header="Update Failed",
            icon="danger",
            color="danger",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 99999}
        )
        return toast, dash.no_update


