@app.callback(
    Output('toast-container', 'children', allow_duplicate=True),
    Input('bulk-trust-all-btn', 'n_clicks'),
    prevent_initial_call=True
)
def bulk_trust_all_unknown(n_clicks):
    """Trust all unknown/unclassified devices."""
    if not n_clicks:
        raise dash.exceptions.PreventUpdate

    try:
        conn = get_db_connection()
        if not conn:
            raise Exception("Database unavailable")

        cursor = conn.cursor()
        cursor.execute('''
            UPDATE devices SET is_trusted = 1
            WHERE is_trusted = 0 AND is_blocked = 0
        ''')
        count = cursor.rowcount
        conn.commit()
        conn.close()

        toast = dbc.Toast(
            [html.I(className="fa fa-check-circle me-2"), f"Trusted {count} unknown device(s)!"],
            header="Bulk Trust Complete",
            icon="success",
            color="success",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 999999}
        )
        return toast

    except Exception as e:
        logger.error(f"Error trusting all: {e}")
        toast = dbc.Toast(
            [html.I(className="fa fa-exclamation-triangle me-2"), f"Failed: {str(e)}"],
            header="Error",
            icon="danger",
            color="danger",
            duration=4000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 999999}
        )
        return toast


# Bulk Block All Suspicious Devices
@app.callback(
    Output('toast-container', 'children', allow_duplicate=True),
    Input('bulk-block-suspicious-btn', 'n_clicks'),
    prevent_initial_call=True
)
def bulk_block_suspicious(n_clicks):
    """Block all suspicious devices (those with alerts)."""
    if not n_clicks:
        raise dash.exceptions.PreventUpdate

    try:
        conn = get_db_connection()
        if not conn:
            raise Exception("Database unavailable")

        cursor = conn.cursor()
        # Block devices that have critical/high alerts
        cursor.execute('''
            UPDATE devices SET is_blocked = 1
            WHERE device_ip IN (
                SELECT DISTINCT device_ip FROM alerts
                WHERE severity IN ('critical', 'high')
            )
        ''')
        count = cursor.rowcount
        conn.commit()
        conn.close()

        toast = dbc.Toast(
            [html.I(className="fa fa-ban me-2"), f"Blocked {count} suspicious device(s)!"],
            header="Bulk Block Complete",
            icon="danger",
            color="danger",
            duration=3000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 999999}
        )
        return toast

    except Exception as e:
        logger.error(f"Error blocking suspicious: {e}")
        toast = dbc.Toast(
            [html.I(className="fa fa-exclamation-triangle me-2"), f"Failed: {str(e)}"],
            header="Error",
            icon="danger",
            color="danger",
            duration=4000,
            is_open=True,
            dismissable=True,
            style={"position": "fixed", "top": 20, "left": "50%", "transform": "translateX(-50%)", "width": 350, "zIndex": 999999}
        )
        return toast


@app.callback(
    [Output('bulk-trust-btn', 'disabled'),
     Output('bulk-block-btn', 'disabled'),
     Output('bulk-delete-btn', 'disabled'),
     Output('selected-count-display', 'children')],
    Input({'type': 'device-checkbox', 'ip': ALL}, 'value')
)
def toggle_bulk_buttons(checkbox_values):
    """Enable/disable bulk action buttons based on selections and update count"""
    selected_count = sum(1 for val in checkbox_values if val) if checkbox_values else 0
    has_selection = selected_count > 0
    # Disabled = NOT has_selection
    return not has_selection, not has_selection, not has_selection, str(selected_count)



@app.callback(
    Output({'type': 'device-checkbox', 'ip': ALL}, 'checked'),
    Input('select-all-devices-checkbox', 'checked'),
    prevent_initial_call=True
)
def select_all_devices(select_all):
    """Select or deselect all device checkboxes"""
    if select_all is None:
        return dash.no_update

    # Get all device checkbox IDs
    all_checkbox_ids = callback_context.outputs_list[0]['id']

