            diagnostics = []

            # Check database
            conn = get_db_connection()
            if conn:
                diagnostics.append("‚úì Database: OK")
                conn.close()
            else:
                diagnostics.append("‚úó Database: FAILED")

            # Check Zeek
            zeek_script = project_root / "zeek_capture.py"
